import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './Login.component.html',
  styleUrls: ['./Login.component.css']
})
export class LoginComponent {

  // Methode, die beim Absenden des Formulars aufgerufen wird
  onLogin(email: string, password: string) {
    console.log('Login versucht mit:', email, password);

    // Hier könntest du die Login-Daten an einen Service übergeben, der den Login-Prozess abwickelt.
    // Beispiel: this.authService.login(email, password);
  }
}
<div class="main">
  <input type="checkbox" id="chk" aria-hidden="true">

  <div class="signup">
    <form>
      <label for="chk" aria-hidden="true">Sign up</label>
      <input type="text" name="txt" placeholder="User name" required="">
      <input type="email" name="email" placeholder="Email" required="">
      <input type="number" name="broj" placeholder="BrojTelefona" required="">
      <input type="password" name="pswd" placeholder="Password" required="">
      <button>Sign up</button>
    </form>
  </div>

  <div class="login">
    <form>
      <label for="chk" aria-hidden="true">Login</label>
      <input type="email" name="email" placeholder="Email" required="">
      <input type="password" name="pswd" placeholder="Password" required="">
      <button>Login</button>
    </form>
  </div>
</div>
