import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './Login.component.html',
  styleUrls: ['./Login.component.css'],
})
export class LoginComponent {
  benutzername: string = '';
  passwort: string = '';
  isPasswordVisible: boolean = false;

  constructor(private http: HttpClient, private router: Router) {}

  togglePasswordVisibility(): void {
    this.isPasswordVisible = !this.isPasswordVisible;
  }

  onSubmit(): void {
    const loginData = {
      benutzername: this.benutzername,
      passwort: this.passwort,
    };

    this.http.post<any>('https://localhost:5001/api/auth/login', loginData).subscribe(
      (response) => {
        // Handle success
        console.log('Login successful', response);
        this.router.navigate(['/blog-list']);
      },
      (error) => {
        // Handle error
        console.error('Login failed', error);
      }
    );
  }
}
<div class="container">
  <h2>Anmeldung</h2>
  <form (ngSubmit)="onSubmit()" #loginForm="ngForm">
    <div class="form-group">
      <input
        type="text"
        name="benutzername"
        [(ngModel)]="benutzername"
        placeholder="Benutzername"
        required
        class="form-control"
      />
    </div>
    <div class="form-group" style="position: relative;">
      <input
        type="password"
        name="passwort"
        [(ngModel)]="passwort"
        placeholder="Passwort"
        required
        class="form-control"
      />
      <span
        class="eye-icon"
        (click)="togglePasswordVisibility()"
        style="cursor: pointer; position: absolute; right: 10px; top: 50%; transform: translateY(-50%);"
      >
        <i [ngClass]="isPasswordVisible ? 'fas fa-eye-slash' : 'fas fa-eye'"></i>
      </span>
    </div>
    <button type="submit" class="btn btn-primary">Anmelden</button>
  </form>
  <p>Noch nicht registriert? <a routerLink="/registrieren">Hier registrieren</a></p>
</div>
