import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './create-artikel.component.html',
  styleUrls: ['./create-artikel.component.css']
})
export class CreateArtikelComponent {
  blog = {
    title: '',
    content: ''
  };

  newCategory = '';
  categories: string[] = [];
  images: { preview: string, file: File }[] = [];
  contextMenu = { visible: false, x: 0, y: 0 };

  submitBlogForm() {
    // Hier kommt die Logik f端r das Absenden des Formulars, wie ein API-Aufruf.
    console.log('Formular abgesendet', this.blog, this.categories, this.images);
  }

  addCategory() {
    if (this.newCategory) {
      this.categories.push(this.newCategory);
      this.newCategory = '';
    }
  }

  removeCategory(index: number) {
    this.categories.splice(index, 1);
  }

  onFilesSelected(event: any) {
    const files = event.target.files;
    for (let i = 0; i < files.length; i++) {
      const file = files[i];
      const reader = new FileReader();
      reader.onload = (e: any) => {
        this.images.push({ preview: e.target.result, file });
      };
      reader.readAsDataURL(file);
    }
  }

  onDragStart(event: DragEvent, index: number) {
    event.dataTransfer?.setData('text/plain', index.toString());
  }

  onDragOver(event: DragEvent) {
    event.preventDefault();
  }

  onDrop(event: DragEvent, targetIndex: number) {
    event.preventDefault();
    const draggedIndex = Number(event.dataTransfer?.getData('text/plain'));
    if (draggedIndex !== targetIndex) {
      const draggedImage = this.images[draggedIndex];
      this.images.splice(draggedIndex, 1);
      this.images.splice(targetIndex, 0, draggedImage);
    }
  }

  onRightClick(event: MouseEvent, index: number) {
    event.preventDefault();
    this.contextMenu.visible = true;
    this.contextMenu.x = event.clientX;
    this.contextMenu.y = event.clientY;
  }

  removeImage() {
    const index = this.contextMenu.x; // Hier solltest du die richtige Logik f端r das Bild entfernen haben
    this.images.splice(index, 1);
    this.contextMenu.visible = false;
  }
}
