import { Component } from '@angular/core';

@Component({
  selector: 'app-login-signup',
  templateUrl: './login-signup.component.html',
  styleUrls: ['./login-signup.component.css']
})
export class LoginSignupComponent {
  // Datenbindung für SignUp und Login Form
  signupData = {
    email: '',
    username: '',
    password: '',
    confirm: false
  };

  loginData = {
    username: '',
    password: ''
  };

  // Toggle für Form-Ansicht
  currentForm: string = 'signup';  // start with 'signup'

  // Funktionen für das Absenden der Formulare
  onSignUp(): void {
    console.log('Sign Up data:', this.signupData);
    // Hier kann eine API zum Erstellen eines Benutzerkontos aufgerufen werden
  }

  onLogin(): void {
    console.log('Login data:', this.loginData);
    // Hier kann eine API zum Login aufgerufen werden
  }

  // Funktion zum Wechseln zwischen Login und Signup Formularen
  toggleForm(form: string): void {
    this.currentForm = form;
    // Hier kann eine Animation oder ein Übergang eingefügt werden
  }
}
<div id="back">
  <canvas id="canvas" class="canvas-back"></canvas>
  <div class="backRight"></div>
  <div class="backLeft"></div>
</div>

<div id="slideBox">
  <div class="topLayer">
    <div class="left">
      <div class="content">
        <h2>Sign Up</h2>
        <form id="form-signup" (ngSubmit)="onSubmitSignup()" #signupForm="ngForm">
          <div class="form-element form-stack">
            <label for="email" class="form-label">Email</label>
            <input id="email" name="email" [(ngModel)]="signup.email" required email>
          </div>
          <div class="form-element form-stack">
            <label for="username-signup" class="form-label">Username</label>
            <input id="username-signup" name="username" [(ngModel)]="signup.username" required>
          </div>
          <div class="form-element form-stack">
            <label for="password-signup" class="form-label">Password</label>
            <input id="password-signup" type="password" name="password" [(ngModel)]="signup.password" required>
          </div>
          <div class="form-element form-checkbox">
            <input id="confirm-terms" type="checkbox" name="confirm" [(ngModel)]="signup.confirm" required>
            <label for="confirm-terms">I agree to the <a href="#">Terms of Service</a> and <a href="#">Privacy Policy</a></label>
          </div>
          <div class="form-element form-submit">
            <button id="signUp" class="signup" type="submit" [disabled]="!signupForm.valid">Sign up</button>
            <button id="goLeft" class="signup off" (click)="toggleForm()">Log In</button>
          </div>
        </form>
      </div>
    </div>
    <div class="right">
      <div class="content">
        <h2>Login</h2>
        <form id="form-login" (ngSubmit)="onSubmitLogin()" #loginForm="ngForm">
          <div class="form-element form-stack">
            <label for="username-login" class="form-label">Username</label>
            <input id="username-login" name="username" [(ngModel)]="login.username" required>
          </div>
          <div class="form-element form-stack">
            <label for="password-login" class="form-label">Password</label>
            <input id="password-login" type="password" name="password" [(ngModel)]="login.password" required>
          </div>
          <div class="form-element form-submit">
            <button id="logIn" class="login" type="submit" [disabled]="!loginForm.valid">Log In</button>
            <button id="goRight" class="login off" (click)="toggleForm()">Sign Up</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
