import { Component } from '@angular/core';

@Component({
  selector: 'app-login-signup',
  templateUrl: './login-signup.component.html',
  styleUrls: ['./login-signup.component.css']
})
export class LoginSignupComponent {
  isSignup = false; // Default to login form

  loginData = {
    email: '',
    password: ''
  };

  signupData = {
    username: '',
    email: '',
    phone: '',
    password: ''
  };

  // Toggle between Login and Signup
  toggleForm() {
    this.isSignup = !this.isSignup;
  }

  // Handle Login form submission
  onSubmitLogin() {
    if (this.loginData.email && this.loginData.password) {
      console.log('Login Data:', this.loginData);
      // Handle your login logic here
    } else {
      console.log('Please fill out the form correctly.');
    }
  }

  // Handle Signup form submission
  onSubmitSignup() {
    if (this.signupData.username && this.signupData.email && this.signupData.phone && this.signupData.password) {
      console.log('Signup Data:', this.signupData);
      // Handle your signup logic here
    } else {
      console.log('Please fill out the form correctly.');
    }
  }
}
<div id="back">
  <canvas id="canvas" class="canvas-back"></canvas>
  <div class="backRight"></div>
  <div class="backLeft"></div>
</div>

<div id="slideBox">
  <div class="topLayer">
    <div class="left">
      <div class="content">
        <h2>Sign Up</h2>
        <form id="form-signup" (ngSubmit)="onSignup()" #signupForm="ngForm">
          <div class="form-element form-stack">
            <label for="email" class="form-label">Email</label>
            <input id="email" type="email" name="email" ngModel>
          </div>
          <div class="form-element form-stack">
            <label for="username-signup" class="form-label">Username</label>
            <input id="username-signup" type="text" name="username" ngModel>
          </div>
          <div class="form-element form-stack">
            <label for="password-signup" class="form-label">Password</label>
            <input id="password-signup" type="password" name="password" ngModel>
          </div>
          <div class="form-element form-checkbox">
            <input id="confirm-terms" type="checkbox" name="confirm" value="yes" class="checkbox" ngModel>
            <label for="confirm-terms">I agree to the <a href="#">Terms of Service</a> and <a href="#">Privacy Policy</a></label>
          </div>
          <div class="form-element form-submit">
            <button id="signUp" class="signup" type="submit">Sign up</button>
            <button id="goLeft" class="signup off" (click)="goLeft()">Log In</button>
          </div>
        </form>
      </div>
    </div>
    <div class="right">
      <div class="content">
        <h2>Login</h2>
        <form id="form-login" (ngSubmit)="onLogin()" #loginForm="ngForm">
          <div class="form-element form-stack">
            <label for="username-login" class="form-label">Username</label>
            <input id="username-login" type="text" name="username" ngModel>
          </div>
          <div class="form-element form-stack">
            <label for="password-login" class="form-label">Password</label>
            <input id="password-login" type="password" name="password" ngModel>
          </div>
          <div class="form-element form-submit">
            <button id="logIn" class="login" type="submit">Log In</button>
            <button id="goRight" class="login off" (click)="goRight()">Sign Up</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
