import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  benutzername: string = '';
  passwort: string = '';
  passwordFieldType: string = 'password';

  constructor(private http: HttpClient, private router: Router) {}

  // Funktion zum Umschalten der Passwortsichtbarkeit
  togglePasswordVisibility() {
    this.passwordFieldType = this.passwordFieldType === 'password' ? 'text' : 'password';
  }

  // Funktion zum Absenden des Formulars
  onSubmit() {
    const loginData = {
      Username: this.benutzername,
      Password: this.passwort
    };

    // API-Aufruf zum Login
    this.http.post('http://localhost:5000/api/login', loginData).subscribe(
      (response: any) => {
        if (response.success) {
          sessionStorage.setItem('userId', response.userId); // Benutzer-ID in der Session speichern
          this.router.navigate(['/Shop']); // Weiterleitung zum Shop
        } else {
          alert('Falsche Anmeldedaten');
        }
      },
      (error) => {
        console.error('Login fehlgeschlagen', error);
        alert('Ein Fehler ist aufgetreten');
      }
    );
  }
}
<div class="container">
  <h2>Anmeldung</h2>
  <form (ngSubmit)="onSubmit()" #loginForm="ngForm">
    <input
      type="text"
      name="benutzername"
      [(ngModel)]="benutzername"
      placeholder="Benutzername"
      required
    />
    <div style="position: relative;">
      <input
        type="{{passwordFieldType}}"
        id="passwort"
        name="passwort"
        [(ngModel)]="passwort"
        placeholder="Passwort"
        required
        class="password-field"
      />
      <span
        class="eye-icon"
        (click)="togglePasswordVisibility()"
        style="cursor: pointer; position: absolute; right: 10px; top: 50%; transform: translateY(-50%);"
      >
                <i class="fas" [ngClass]="passwordFieldType === 'password' ? 'fa-eye' : 'fa-eye-slash'"></i>
            </span>
    </div>
    <button type="submit" [disabled]="!loginForm.valid">Anmelden</button>
  </form>
  <p>Noch nicht registriert? <a routerLink="/registrieren">Hier registrieren</a></p>
</div>
