import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  benutzername: string = '';
  passwort: string = '';
  isPasswordVisible: boolean = false;

  constructor(private http: HttpClient, private router: Router) {}

  togglePasswordVisibility(): void {
    this.isPasswordVisible = !this.isPasswordVisible;
  }

  onSubmit(): void {
    const loginData = {
      benutzername: this.benutzername,
      passwort: this.passwort,
    };

    this.http.post<any>('https://localhost:5001/api/auth/login', loginData).subscribe(
      (response) => {
        // Handle success
        console.log('Login successful', response);
        this.router.navigate(['/blog-list']);
      },
      (error) => {
        // Handle error
        console.error('Login failed', error);
      }
    );
  }
}
<div class="container">
  <h2>Anmeldung</h2>

  <!-- Login-Formular -->
  <form (ngSubmit)="onSubmit()">
    <!-- Benutzername-Feld -->
    <input type="text" [(ngModel)]="benutzername" name="benutzername" placeholder="Benutzername" required>

    <!-- Passwortfeld mit Augen-Symbol -->
    <div style="position: relative;">
      <input
        type="{{ isPasswordVisible ? 'text' : 'password' }}"
        [(ngModel)]="passwort"
        name="passwort"
        placeholder="Passwort"
        required
        class="password-field">

      <!-- Augen-Symbol -->
      <span
        class="eye-icon"
        (click)="togglePasswordVisibility()"
        style="cursor: pointer; position: absolute; right: 10px; top: 50%; transform: translateY(-50%);">
        <i class="fas" [ngClass]="isPasswordVisible ? 'fa-eye-slash' : 'fa-eye'"></i>
      </span>
    </div>

    <!-- Submit-Button -->
    <input type="submit" value="Anmelden">
  </form>

  <!-- Link zur Registrierung -->
  <p>Noch nicht registriert? <a routerLink="/registrieren">Hier registrieren</a></p>
</div>
