import { Component, OnInit, AfterViewInit } from '@angular/core';
import * as paper from 'paper';

@Component({
  selector: 'app-login-signup',
  templateUrl: './login-signup.component.html',
  styleUrls: ['./login-signup.component.scss']
})
export class LoginSignupComponent implements OnInit, AfterViewInit {

  signup = { email: '', username: '', password: '', confirm: false };
  login = { username: '', password: '' };

  constructor() { }

  ngOnInit(): void {}

  ngAfterViewInit(): void {
    this.initializeCanvas();
  }

  initializeCanvas(): void {
    const canvas = document.getElementById('canvas') as HTMLCanvasElement;
    if (canvas) {
      paper.setup(canvas);  // Paper.js Setup auf dem Canvas-Element
      this.createShapes();
    }
  }

  createShapes(): void {
    const canvasWidth = paper.view.size.width;
    const canvasHeight = paper.view.size.height;

    const positionArray = [
      { x: canvasWidth / 2, y: 100 },
      { x: 200, y: canvasHeight / 2 },
      { x: canvasWidth - 130, y: canvasHeight - 75 },
      { x: 50, y: 300 }
    ];

    const shapeGroup = new paper.Group();
    const shapePathData = [
      'M231,352l445-156L600,0L452,54L331,3L0,48L231,352',
      'M0,0l64,219L29,343l535,30L478,37l-133,4L0,0z'
    ];

    shapePathData.forEach((pathData, i) => {
      const headerShape = new paper.Path({
        strokeColor: 'rgba(255, 255, 255, 0.5)',
        strokeWidth: 2,
        parent: shapeGroup
      });

      headerShape.pathData = pathData;
      headerShape.scale(2);

      // Set initial position (this could be the start of the animation)
      headerShape.position = new paper.Point(positionArray[i].x, positionArray[i].y);

      // Animate the shape using Paper.js's onFrame function
      paper.view.onFrame = (event) => {
        // You can animate the position here over time
        headerShape.position.x += 1;
        headerShape.position.y += 1;
      };
    });
  }

  onSubmitSignup(): void {
    console.log('SignUp Form Data:', this.signup);
  }

  onSubmitLogin(): void {
    console.log('Login Form Data:', this.login);
  }

  toggleForm(): void {
    const slideBox = document.getElementById('slideBox')!;
    const topLayer = document.getElementById('topLayer') as HTMLElement;

    if (slideBox.style.marginLeft === '0px') {
      slideBox.style.marginLeft = '50%';
      topLayer.style.marginLeft = '0';
    } else {
      slideBox.style.marginLeft = '0';
      topLayer.style.marginLeft = '100%';
    }
  }
}
<div id="back">
  <canvas id="canvas" class="canvas-back"></canvas>
  <div class="backRight"></div>
  <div class="backLeft"></div>
</div>

<div id="slideBox">
  <div class="topLayer">
    <div class="left">
      <div class="content">
        <h2>Sign Up</h2>
        <form id="form-signup" (ngSubmit)="onSubmitSignup()" #signupForm="ngForm">
          <div class="form-element form-stack">
            <label for="email" class="form-label">Email</label>
            <input id="email" name="email" [(ngModel)]="signup.email" required email>
          </div>
          <div class="form-element form-stack">
            <label for="username-signup" class="form-label">Username</label>
            <input id="username-signup" name="username" [(ngModel)]="signup.username" required>
          </div>
          <div class="form-element form-stack">
            <label for="password-signup" class="form-label">Password</label>
            <input id="password-signup" type="password" name="password" [(ngModel)]="signup.password" required>
          </div>
          <div class="form-element form-checkbox">
            <input id="confirm-terms" type="checkbox" name="confirm" [(ngModel)]="signup.confirm" required>
            <label for="confirm-terms">I agree to the <a href="#">Terms of Service</a> and <a href="#">Privacy Policy</a></label>
          </div>
          <div class="form-element form-submit">
            <button id="signUp" class="signup" type="submit" [disabled]="!signupForm.valid">Sign up</button>
            <button id="goLeft" class="signup off" (click)="toggleForm()">Log In</button>
          </div>
        </form>
      </div>
    </div>
    <div class="right">
      <div class="content">
        <h2>Login</h2>
        <form id="form-login" (ngSubmit)="onSubmitLogin()" #loginForm="ngForm">
          <div class="form-element form-stack">
            <label for="username-login" class="form-label">Username</label>
            <input id="username-login" name="username" [(ngModel)]="login.username" required>
          </div>
          <div class="form-element form-stack">
            <label for="password-login" class="form-label">Password</label>
            <input id="password-login" type="password" name="password" [(ngModel)]="login.password" required>
          </div>
          <div class="form-element form-submit">
            <button id="logIn" class="login" type="submit" [disabled]="!loginForm.valid">Log In</button>
            <button id="goRight" class="login off" (click)="toggleForm()">Sign Up</button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
